var RadioGroup=React.createClass({displayName:"RadioGroup",componentDidMount:function(){this.setRadioNames();this.setCheckedRadio()},componentDidUpdate:function(){this.setRadioNames();this.setCheckedRadio()},render:function(){return this.transferPropsTo(React.DOM.div({onChange:this.props.onChange},this.props.children))},setRadioNames:function(){var e=this.getRadios();for(var t=0,i=e.length;t<i;t++){e[t].setAttribute("name",this.props.name)}},getRadios:function(){return this.getDOMNode().querySelectorAll('input[type="radio"]')},setCheckedRadio:function(){var e=this.getRadios();for(var t=0,i=e.length;t<i;t++){var a=e[t];if(a.value==this.props.value){a.checked=true}}},getCheckedValue:function(){var e=this.getRadios();for(var t=0,i=e.length;t<i;t++){if(e[t].checked){return e[t].value}}return null}});